class FigmaToCodeApp extends StatelessWidget {
  static final AudioPlayer audioPlayer = AudioPlayer();
  //aaa
  bool isPlaying = false;
  String currentMusicPath = "";

  //HAPPY
  List<String> musicFiles = [
    "assets/happy_2.mp3",
    "assets/happy-2.mp3",
  ];

//SAD
  List<String> musicFiles2 = [
    "assets/happy_2.mp3",
    "assets/New_Composition_6.mp3",
    "assets/ANGRY1.mp3",
    "assets/EMOTION1.mp3",
    "assets/peace.mp3",
  ];

//ANGRY
  List<String> musicFiles3 = [
    "assets/happy_2.mp3",
    "assets/New_Composition_6.mp3",
    "assets/ANGRY1.mp3",
    "assets/EMOTION1.mp3",
    "assets/peace.mp3",
  ];

//EMOTION
  List<String> musicFiles4 = [
    "assets/happy_2.mp3",
    "assets/New_Composition_6.mp3",
    "assets/ANGRY1.mp3",
    "assets/EMOTION1.mp3",
    "assets/peace.mp3",
  ];

//PEACE
  List<String> musicFiles5 = [
    "assets/happy_2.mp3",
    "assets/New_Composition_6.mp3",
    "assets/ANGRY1.mp3",
    "assets/EMOTION1.mp3",
    "assets/peace.mp3",
  ];

  void playRandomMusic() async {
    Random random = Random();
    int randomIndex = random.nextInt(musicFiles.length);
    currentMusicPath = musicFiles[randomIndex];
    audioPlayer.play(DeviceFileSource(currentMusicPath));
    isPlaying = true;
  }

  void stopMusic() async {
    await audioPlayer.stop();
    isPlaying = false;
  }

  void toggleMusic() {
    print(isPlaying);
    if (isPlaying == true) {
      stopMusic();
    } else if (isPlaying == false) {
      playRandomMusic();
    }
  }

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      theme: ThemeData.dark().copyWith(
        scaffoldBackgroundColor: const Color.fromARGB(255, 18, 32, 47),
      ),
      home: Scaffold(
        body: ListView(children: [
          Homescreen(),
        ]),
      ),
    );
  }
}

